// Parse the AI response and clean it up
let response = '';

// Try different response formats based on n8n version
if (items[0].json.text) {
  response = items[0].json.text;
} else if (items[0].json.choices && items[0].json.choices[0]) {
  response = items[0].json.choices[0].message?.content || items[0].json.choices[0].text || '';
} else if (items[0].json.message) {
  response = items[0].json.message.content || items[0].json.message;
} else {
  response = JSON.stringify(items[0].json);
}

// Extract JSON from response if it's wrapped in markdown
const jsonMatch = response.match(/```json\s*([\s\S]*?)\s*```/) || response.match(/{[\s\S]*}/);
if (jsonMatch) {
  response = jsonMatch[1] || jsonMatch[0];
}

try {
  const analysis = JSON.parse(response);
  return [{ json: analysis }];
} catch (error) {
  // Fallback if JSON parsing fails
  console.log('Parse error:', error, 'Raw response:', response);
  return [{
    json: {
      action: 'INFO',
      summary: 'Unable to parse conversation analysis. Raw response: ' + response.substring(0, 200),
      userDetails: {
        name: null,
        email: null,
        phone: null,
        preferredTime: null
      },
      priority: 'LOW',
      nextSteps: 'Manual review required - check raw response format'
    }
  }];
}

